import React, { useState } from "react";
import ReactDOM from "react-dom";
import "../styles/RSVPModal.css";

export default function RSVPModal({ open, onClose }) {
  const [data, setData] = useState({
    assistanceConfirmation: "",
    firstAssistant: "",
    secondAssistant: "",
    thirdAssistant: "",
    fourthAssistant: "",
    fifthAssistant: "",
    sixthAssistant: "",
  });

  const {
    assistanceConfirmation,
    firstAssistant,
    secondAssistant,
    thirdAssistant,
    fourthAssistant,
    fifthAssistant,
    sixthAssistant,
  } = data;

  const handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(
        "https://v1.nocodeapi.com/santfernandez/google_sheets/qqHSFkbwufAzEQMf?tabId=Sheet1",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify([
            assistanceConfirmation,
            firstAssistant,
            secondAssistant,
            thirdAssistant,
            fourthAssistant,
            fifthAssistant,
            sixthAssistant,
          ]),
        }
      );
      await response.json();
      setData({
        ...data,
        assistanceConfirmation: "",
        firstAssistant: "",
        secondAssistant: "",
        thirdAssistant: "",
        fourthAssistant: "",
        fifthAssistant: "",
        sixthAssistant: "",
      });
    } catch (err) {
      console.log(err);
    }
  };

  console.log(data);

  if (!open) return null;

  return ReactDOM.createPortal(
    <>
      <div className="rsvp-overlay-modal">
        <div className="rsvp-modal">
          <button className="rsvp-close-modal-button" onClick={onClose}>
            X
          </button>
        </div>
      </div>
    </>,
    document.getElementById("portal")
  );
}
