import React, { useEffect, useState } from "react";
import "../styles/Countdown.css";

const Countdown = () => {
  const calculateTimeLeft = () => {
    let year = new Date().getFullYear();
    const difference = +new Date(`${year}-05-07`) - +new Date();
    let timeLeft = {};

    if (difference > 0) {
      timeLeft = {
        dias: Math.floor(difference / (1000 * 60 * 60 * 24)),
        horas: Math.floor((difference / (1000 * 60 * 60)) % 24),
        minutos: Math.floor((difference / 1000 / 60) % 60),
        segundos: Math.floor((difference / 1000) % 60),
      };
    }

    return timeLeft;
  };

  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());

  useEffect(() => {
    setTimeout(() => {
      setTimeLeft(calculateTimeLeft());
    }, 1000);
  });

  const timerComponents = [];

  Object.keys(timeLeft).forEach((interval) => {
    if (!timeLeft[interval]) {
      return;
    }

    timerComponents.push(
      <span>
        {timeLeft[interval]} {interval}{" "}
      </span>
    );
  });

  return (
    <div className="countdown">
      <div className="hello">
        {timerComponents.length ? timerComponents : <span>Time's up!</span>}
      </div>
      <div className="days">{timerComponents.dias}</div>
      <div className="hours">{timerComponents.horas}</div>
      <div className="minutes">{timerComponents.minutos}</div>
      <div className="seconds">{timerComponents.segundos}</div>
    </div>
  );
};

export default Countdown;
