import React, { useEffect, useRef, useState } from "react";
import "../styles/Home.css";

const Home = () => {
  let string = "¡Nos casamos!";
  let context = string.getContext("2d");

  context.font = "50px serif";
  context.fillStyle = "green";
  context.textAlign = "center";

  let angle = Math.PI * 0.6; // in radians
  let radius = 200;

  context.translate(300, 300);
  context.rotate((-1 * angle) / 2);

  for (let i = 0; i < string.length; i++) {
    context.rotate(angle / string.length);
    context.save();
    context.translate(0, -1 * radius);
    context.fillText(string[i], 0, 0);
    context.restore();
  }
  return (
    <div className="home">
      <div className="home-content">
        <div className="home-content-a">
          <div className="date-and-icon">
            <img
              className="home-icon"
              src={`https://drive.google.com/uc?export=view&id=1pyQr_TMOb5REBhjjJ2yZwy5PTzeFaEyp`}
            />
            <div className="home-phrase">
              <p>{`$string`}</p>
            </div>
          </div>
        </div>
        <div className="home-content-b">
          <div className="home-title">
            <h2>Joaquín</h2>
            <p className="middle-title">&</p>
            <h2>Sofía</h2>
          </div>
        </div>
        <div className="home-content-c">
          <span className="home-date">Sábado 13 de Agosto</span>
        </div>
      </div>
    </div>
  );
};

export default Home;
